                theme={DENSITY_THEME}
                rowData={rowData}
                columnDefs={colDefs}
                domLayout='autoHeight'
             />
        </div>
    </Paper>
  );
}

import { createContext, useContext, useState, type ReactNode } from 'react';

type HeaderCenterState = {
  center: ReactNode;
  setCenter: (node: ReactNode) => void;
};

const HeaderCenterContext = createContext<HeaderCenterState>({
  center: null,
  setCenter: () => {},
});

export function HeaderCenterProvider({ children }: { children: ReactNode }) {
  const [center, setCenter] = useState<ReactNode>(null);
  return (
    <HeaderCenterContext.Provider value={{ center, setCenter }}>
      {children}
    </HeaderCenterContext.Provider>
  );
}

export function useHeaderCenter() {
  return useContext(HeaderCenterContext);
}

import { useEffect, useState } from 'react';
import { supabase } from '@/lib/supabase';
import { TABLES } from '@/lib/tables';
import type { DocumentRow } from '@/lib/types';

export function useDocument(sourceUid: string | undefined) {
  const [doc, setDoc] = useState<DocumentRow | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    if (!sourceUid) { setLoading(false); return; }
    setLoading(true);
    supabase
      .from(TABLES.documents)
      .select('*')
      .eq('source_uid', sourceUid)
      .maybeSingle()
      .then(({ data, error: err }) => {
        if (err) setError(err.message);
        else if (!data) setError('Document not found');
        else setDoc(data as DocumentRow);
        setLoading(false);
      });
  }, [sourceUid]);

  return { doc, loading, error };
}
